--CREATE TABLE
CREATE TABLE NHANVIEN (
MANV VARCHAR(06) PRIMARY KEY,
TENNV VARCHAR(50) NOT NULL,
PHAI VARCHAR(5),
NGAYSINH DATE,
DIACHI VARCHAR(100),
SODT VARCHAR(15),
LUONG RAW(1024),
PHUCAP RAW(1024),
VAITRO VARCHAR(20),
MANQL VARCHAR(06),
PHG VARCHAR(06),
FOREIGN KEY (MANQL) REFERENCES NHANVIEN(MANV)
--FOREIGN KEY (PHG) REFERENCES PHONGBAN(MAPB)
);
/
CREATE TABLE PHONGBAN (
MAPB VARCHAR(06) PRIMARY KEY,
TENPB VARCHAR(50) NOT NULL,
TRPHG VARCHAR(06)
--FOREIGN KEY (TRPHG) REFERENCES NHANVIEN(MANV)
);
/
CREATE TABLE DEAN (
MADA VARCHAR(06) PRIMARY KEY,
TENDA VARCHAR(50) NOT NULL,
NGAYBD DATE,
PHONG VARCHAR(06)
--FOREIGN KEY (PHONG) REFERENCES PHONGBAN(MAPB)
);
/
CREATE TABLE PHANCONG (
MANV VARCHAR(06),
MADA VARCHAR(06),
THOIGIAN DATE,
PRIMARY KEY (MANV, MADA)
--FOREIGN KEY (MANV) REFERENCES NHANVIEN(MANV),
--FOREIGN KEY (MADA) REFERENCES DEAN(MADA)
);
